function sendEprocOrder(eProcUrl, eProcOrderData) {
	"use strict";
	var isNexpart = eProcUrl.includes("nexpart");
	Log.info('eProcUrl: ' + eProcUrl);
	Log.info('eProcOrderData: ' + eProcOrderData);
	Log.info('Nexpart: ' + isNexpart);
	if (isNexpart) {
		Log.info('Sending for to Nexpart using target="_top"');
	    var eProcOrderForm = $('<form method="post" target="_parent" action="' + eProcUrl + '" id="sendEprocOrderForm"><input type="hidden" name="cxml-urlencoded" id="cxml-urlencoded" value="" /></form>');
	} else {
		var eProcOrderForm = $('<form method="post" action="' + eProcUrl + '" id="sendEprocOrderForm"><input type="hidden" name="cxml-urlencoded" id="cxml-urlencoded" value="" /></form>');
	}

	$("body").append(eProcOrderForm);
	$("#cxml-urlencoded").val(eProcOrderData);
	eProcOrderForm.submit();
}

